import React from 'react';
import './Services.css';

const Services = () => {
  return (
    <div className="services">
      <h1>Services</h1>
      <div className="services-grid">
        <ServiceBox icon="balance-scale" title="Accounting" description="Audit & Assurance services" />
        <ServiceBox icon="book" title="Tax" description="Professional accounting services" />
        <ServiceBox icon="calculator" title="Book" description="Tax planning & preparation" />
        <ServiceBox icon="handshake-o" title="Advisory" description="Business advisory services" />
      </div>
    </div>
  );
};

const ServiceBox = ({ icon, title, description }) => (
  <div className="service-box">
    <i className={`fa fa-${icon} service-icon`} aria-hidden="true"></i>
    <h2 className="service-title">{title}</h2>
    <p className="service-description">{description}</p>
  </div>
);

export default Services;
